let bars=document.querySelector(".link-menu"),links=document.querySelector(".links"),closeButton=document.querySelector(".close");bars.onclick=function(){links.classList.add("show"),bars.classList.remove("show")},closeButton.onclick=function(){links.classList.remove("show"),bars.classList.add("show")},document.addEventListener("click",s=>{links.contains(s.target)||bars.contains(s.target)||(links.classList.remove("show"),bars.classList.add("show"))}),document.onscroll=function(){links.classList.remove("show"),bars.classList.add("show"),checkScroll()};let icons=document.querySelectorAll(".services-icon");document.addEventListener("click",function(s){icons.forEach(t=>{t.contains(s.target)&&(icons.forEach(s=>s.classList.remove("active")),t.classList.add("active"))})});let projects=document.querySelectorAll(".project");function showBox(s){let t=document.querySelector(".show-box");t.innerHTML="",t.innerHTML=s.innerHTML}document.addEventListener("click",function(s){projects.forEach(t=>{t.contains(s.target)&&(projects.forEach(s=>s.classList.remove("active")),t.classList.add("active"),showBox(t))})});let copyrightYear=document.querySelector(".copyright-year");copyrightYear.textContent=new Date().getFullYear();let button=document.querySelector(".scroll-top");function checkScroll(){window.scrollY>500?(button.style.display="flex",setTimeout(()=>{button.classList.add("show")},300)):(button.classList.remove("show"),button.style.display="none")}document.addEventListener("click",s=>{button.contains(s.target)&&window.scrollTo({top:0,behavior:"smooth"})});